import "./Home.css";
import { useState, useEffect } from "react";
import logo from "./logo192.png";
import intro from "./images/intro.jpeg";
import weather from "./images/weatherlogo.png";
import {Link} from "react-router-dom";
import flappy from "./images/flappy.png";
import fridge from "./images/fridge.jpeg";
import holidays from "./images/holidays.jpg";


function fadein() {
  
}
export function Header() {
  const [prevScrollPos, setPrevScrollPos] = useState(window.scrollY);

  useEffect(() => {
    const handleScroll = () => {
      const currentScrollPos = window.scrollY;
      const header = document.querySelector(".header");
      if (currentScrollPos > prevScrollPos) {
        header.classList.add("scrolled");
      } else {
        header.classList.remove("scrolled");
      }

      setPrevScrollPos(currentScrollPos);
    };

    window.addEventListener("scroll", handleScroll);

    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, [prevScrollPos]);

  return (
    <div className="home">
      <div className="header">
        <nav>
        <div className="header-name">
          <Link to="/home">
            <p>Jimmy Tran</p>
          </Link>
        </div>
        <div className="header-work">
        <Link to="/work">
            <p>Work</p>
        </Link>
        </div>
        <div className="header-about">
        <Link to="/about">
            <p>About</p>
          </Link>
        </div>
        <div className="header-contact">
        <Link to="/contact">
            <p>Contact</p>
          </Link>
        </div>
        </nav>
      </div>
    </div>
  );
}

function Desc() {
    useEffect(() => {
        const introElement = document.querySelector(".intro-section");
        const observer = new IntersectionObserver(
          ([entry]) => {
            if (entry.isIntersecting) {
              introElement.classList.add("fade-in");
            } else {
              introElement.classList.remove("fade-in");
            }
          },
          {
            threshold: 0.3, // Adjust this value as needed
          }
        );
    
        if (introElement) {
          observer.observe(introElement);
        }
    
        return () => {
          if (introElement) {
            observer.unobserve(introElement);
          }
        };
      }, []);

  return (
    <div className="intro-section">
      <div className="intro-desc">
        <p>Jimmy Tran</p>
        <p>Front-end Web Developer</p>
      </div>
      <div className="intro-img">
        <img src={intro}></img>
      </div>
    </div>
  );
}


function Intro() {
  useEffect(() => {
    const introElement = document.querySelector(".intro");
    const observer = new IntersectionObserver(
      ([entry]) => {
        if (entry.isIntersecting) {
          introElement.classList.add("fade-in");
        } else {
          introElement.classList.remove("fade-in");
        }
      },
      {
        threshold: 0.3, // Adjust this value as needed
      }
    );

    if (introElement) {
      observer.observe(introElement);
    }

    return () => {
      if (introElement) {
        observer.unobserve(introElement);
      }
    };
  }, []);

  return (
    <div className="intro">
        <Desc />
    </div>
  );
}

function Grid() {
    const images = [flappy, fridge, holidays];
    const desc = ["Flappy Bird", "Virtual IoT Device", "Holiday Tracker" ];
    const [currentImage, setCurrentImage] = useState(0);

    useEffect(() => {
      
      const interval = setInterval(() => {
          setCurrentImage((prevIndex) => (prevIndex + 1) % images.length);
      }, 3000); // Change image every 3 seconds

      return () => clearInterval(interval);
  }, [images.length]);


    useEffect(() => {
        const introElement = document.querySelector(".projects-grid");
        const observer = new IntersectionObserver(
          ([entry]) => {
            if (entry.isIntersecting) {
              introElement.classList.add("fade-in");
            } else {
              introElement.classList.remove("fade-in");
            }
          },
          {
            threshold: 0.3, 
          }
        );
    
        if (introElement) {
          observer.observe(introElement);
        }
    
        return () => {
          if (introElement) {
            observer.unobserve(introElement);
          }
        };
      }, []);

      

    return (
      <div className="project-outer">
        <div className="projects-grid">
        <div className="project">
          <img src={images[currentImage]}></img>
        </div>
      </div>
      <div className="project disc">
          <p>Projects</p>
        </div>
      </div>
    )
}

function Projects() {
    useEffect(() => {
        const introElement = document.querySelector(".projects");
        const observer = new IntersectionObserver(
          ([entry]) => {
            if (entry.isIntersecting) {
              introElement.classList.add("fade-in");
            } else {
              introElement.classList.remove("fade-in");
            }
          },
          {
            threshold: 0.3, // Adjust this value as needed
          }
        );
    
        if (introElement) {
          observer.observe(introElement);
        }
    
        return () => {
          if (introElement) {
            observer.unobserve(introElement);
          }
        };
      }, []);

  return (
    <div className="projects">
      <Grid />
    </div>
  );
}

function Footer() {
    return (
        <div className="footer">
        <ul className="socialicons">
          <li>
            <a
              href="https://www.linkedin.com/in/jimmy-tran1023/"
              target="_blank"
              className="transition"
              >&#xeac9;</a>
          </li>
          <li>
            <a
              href="https://github.com/jimbucktwo"
              target="_blank"
              className="transition"
              >&#xeab0;</a>
          </li>
          <li>
            <a
              href="https://www.instagram.com/jimothytran/"
              target="_blank"
              className="transition"
              >&#xea92;</a>
          </li>
          <li>
            <a
              href="https://replit.com/@JimmyTran26"
              target="_blank"
              className="transition"
              >&#xeaa7;</a>
          </li>
        </ul>
            <p>Jimmy Tran - Web Developer</p>
        </div>
    );

}

function Resume() {
  return(
    <div className="resume">

    </div>
  )
}
export default function Home() {
  return (
    <div>
      <Header />
      <Intro />
      <Projects />
      <Footer />
    </div>
  );
}
