import './Work.css';
import {Header, Footer} from './Home';
import {useEffect} from 'react';
import work from "./images/work.jpg";

function Workpage() {
  useEffect(() => {
    const introElement = document.querySelector(".work");
    const observer = new IntersectionObserver(
      ([entry]) => {
        if (entry.isIntersecting) {
          introElement.classList.add("fade-in");
        } else {
          introElement.classList.remove("fade-in");
        }
      },
      {
        threshold: 0.3, // Adjust this value as needed
      }
    );

    if (introElement) {
      observer.observe(introElement);
    }

    return () => {
      if (introElement) {
        observer.unobserve(introElement);
      }
    };
  }, []);

  return (
    <div className="work">
      <h2></h2>
    </div>
  );
}

function Instructor(){
  useEffect(() => {
    const introElement = document.querySelector(".instructor");
    const observer = new IntersectionObserver(
      ([entry]) => {
        if (entry.isIntersecting) {
          introElement.classList.add("fade-in");
        } else {
          introElement.classList.remove("fade-in");
        }
      },
      {
        threshold: 0.3, // Adjust this value as needed
      }
    );

    if (introElement) {
      observer.observe(introElement);
    }

    return () => {
      if (introElement) {
        observer.unobserve(introElement);
      }
    };
  }, []);

    return(
        <div className="instructor">
        </div>);
}

export default function Work() {
    return (
        <div>
        <Header/>
        <Workpage/>
        <Instructor/>
        <Footer/>
        </div>
    );
}
